using System.Collections.Generic;

namespace CAC.client
{
    /// <summary>
    /// 一种表情的集合。
    /// </summary>
    class EmojisCollection
    {
        public string Name { get; set; }
        public string Description { get; set; }
        public List<EmojisGroup> Groups { get; set; }
    }

    /// <summary>
    /// 一种表情中的一个分组。
    /// </summary>
    class EmojisGroup
    {
        //组名
        public string Title { get; set; }
        //该组的标志符号
        public string Glyph { get; set; }

        public List<Emoji> Items { get; set; }

        public int Count {
            get {
                return Items.Count;
            }
        }
        public static List<Emoji> ItemsFromArray(params string[][] arrays)
        {
            var list = new List<Emoji>();
            foreach(string[] array in arrays) {
                foreach (string s in array) {
                    list.Add(new Emoji(s, ""));
                }
            }
            return list;
        }
    }

    /// <summary>
    /// 单个的表情。
    /// </summary>
    class Emoji
    {
        public string EmojiString { get; set; }
        public string Description { get; set; }
        public Emoji(string EmojiString, string Description)
        {
            this.EmojiString = EmojiString;
            this.Description = Description;
        }
    }

